mapper.select:
SELECT BOARD_CATE_CODE, BOARD_DATE, BOARD_TITLE, BOARD_CONTENT, BOARD_PHOTO
FROM BOARDVIEW
WHERE BOARD_CODE = #{board_code} AND BOARD_WRITER = #{board_writer}

SELECT *
FROM BOARDVIEW
WHERE BOARD_CODE = #{board_code} AND BOARD_WRITER = #{board_writer}

SELECT *
FROM BOARDVIEW
WHERE BOARD_CODE = #{board_code} AND BOARD_WRITER = #{board_writer}

mapper.update:
UPDATE TBL_BOARD
SET BOARD_CATE_CODE = #{board_cate_code}
  , BOARD_DATE = SYSDATE
  , BOARD_TITLE = #{board_title}
  , BOARD_CONTENT = #{board_content}
  , BOARD_PHOTO = #{board_photo, jdbcType=VARCHAR}
WHERE BOARD_CODE = #{board_code} AND BOARD_WRITER = #{board_writer}

mapper.select:
SELECT BOARD_CODE
FROM BOARDVIEW
WHERE BOARD_CODE = #{board_code} AND BOARD_WRITER = #{board_writer}
